package Practice;

public class StringLetterreversal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name="ABC PQR XYZ";
		String[] b=name.split(" ");
		for(int i=b.length-1;i>=0;i--)
		{
			System.out.print(b[i]);
			System.out.print(" ");
		}
		
		
		
	}

}
